import React,{useState} from "react";
import "./style.css";
import News from "./news";
import Form from "./form";




const Navbar = () => {


    const [showModel, setShowModel] = useState("close")
    const handleClick= (e) => {
        e.preventDefault()
    
        switch(showModel) {
            case "open":
                setShowModel("close")
                break;
                case "close":
                setShowModel("open")
                break;
                default:
                    setShowModel("close")
        }
    
    }

  return (
  <>
  <div className="main-div">
   <section className="section-div ">
          <div className="first-s-div">
            Hi reader.
            <img src="" alt="" />
            <span className="f-s-d-s">Here's Your News!</span>
          </div>
          <div className="second-s-div">
            <h3>View Toggel</h3>
            <button className="s-s-d-button">
              <img src="\images\horizontal_split.svg" alt="" />
            </button>
            <button className="s-s-d-button">
              <img src="\images\vertical_split.svg" alt="" />
            </button>
          </div>

          <div className="third-s-div">
            Have a Feedback?
            <button className="t-s-d-button" onClick={handleClick}>
              <span>We're Listening!</span>
            </button>
          </div>
        </section>

        <News />
        {
            showModel==="open" && <Form />
        }
        
        </div>
  </>
  )
};

export default Navbar ;
