import React, { useState } from "react";
import "./style.css";
import News from "./news";
import Form from "./form";
import Newsverticle from "./newsverticle";

const Navbar = () => {
  const [view, setView] = useState("horizontal");
  const [showModel, setShowModel] = useState("close");
  const indexOfLastPost = currentPage * postPerPage;
  const indexOfFirstPost = indexOfLastPost - postPerPage;
  const currentPosts = user.slice(indexOfFirstPost, indexOfLastPost);

  const handleClick = (e) => {
    e.preventDefault();

    switch (showModel) {
      case "open":
        setShowModel("close");
        break;
      case "close":
        setShowModel("open");
        break;
      default:
        setShowModel("close");
        break;
    }
  };

  return (
    <>
      <div className="main-div">
        <section className="section-div ">
          <div className="first-s-div">
            Hi reader.
            <img src="" alt="" />
            <span className="f-s-d-s">Here's Your News!</span>
          </div>

          <div className="second-s-div">
            <h3>View Toggel</h3>
            <button
              className="s-s-d-button"
              onClick={() => setView("horizontal")}
            >
              <img src="\images\horizontal_split.svg" alt="" />
            </button>
            <button
              className="s-s-d-button"
              onClick={() => setView("verticle")}
            >
              <img src="\images\vertical_split.svg" alt="" />
            </button>
          </div>

          <div className="third-s-div">
            Have a Feedback?
            <button className="t-s-d-button" onClick={handleClick}>
              <span>We're Listening!</span>
            </button>
          </div>

          <Form showModel={showModel} handleClick={handleClick} />
        </section>

        {view === "verticle" ? <Newsverticle /> : <News />}
      </div>
    </>
  );
};

export default Navbar;
