import React, { useEffect, useState } from "react";
import "./style.css";

const Newsverticle = () => {
  const [user, setUser] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [postPerPage] = useState(5);

  const getUser = async () => {
    const response = await fetch("https://jsonplaceholder.typicode.com/posts");
    setUser(await response.json());
  };

  useEffect(() => {
    getUser();
  }, []);

  //pagination
  const pageNumber = [];

  for (let i = 1; i <= 3; i++) {
    pageNumber.push(i);
  }

  // get Current post
  const indexOfLastPost = currentPage * postPerPage;
  const indexOfFirstPost = indexOfLastPost - postPerPage;
  const currentPosts = user.slice(indexOfFirstPost, indexOfLastPost);

  // changePage
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  // function
  const handlePrev = () => {
    const page = currentPage - 1;
    setCurrentPage(page);
  };
  const handleNext = () => {
    const page = currentPage + 1;
    setCurrentPage(page);
  };
  return (
    <>
      <div>
        <div className="news-div-2">
          {currentPosts.map((curEle) => {
            return (
              <div>
              <div className="news-c-div-2" key={curEle.id}>
                <h1>{curEle.title}</h1>
                <p>{curEle.body}</p>

               
              </div>
              <div className="img-div-2">
                  <img src="\images\close.svg" alt="" />
                </div>
              </div>
            );
          })}
        </div>
        <nav className="nav-m-div-2">
          <ul className="pagination nav-div">
            <li>
              <button
                onClick={handlePrev}
                disabled={currentPage === 1}
                className="nav-button"
              >
                <img src="\images\arrow_back.svg" alt="" />
              </button>
            </li>
            {pageNumber.map((number) => (
              <li key={number} className="page-item ">
                <a
                  href="!#"
                  onClick={() => paginate(number)}
                  className="page-link nav-li"
                >
                  {number}
                </a>
              </li>
            ))}

            <li>
              <button
                onClick={handleNext}
                disabled={currentPage === 17}
                className="nav-button"
              >
                <img src="\images\arrow_forward.svg" alt="" />
              </button>{" "}
            </li>
          </ul>
        </nav>
      </div>
    </>
  );
};

export default Newsverticle;
