import React, { useEffect, useState } from "react";
import "./style.css";

const News = () => {
  const [user, setUser] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [postPerPage, setPostPerPage]= useState(6)


  const getUser = async () => {
    const response = await fetch("https://jsonplaceholder.typicode.com/posts");
    setUser(await response.json());
  };

  useEffect(() => {
    getUser();
  }, []);
  
  //pagination 
  const pageNumber = [];
   
  for (let i=1; i<=3; i++){
      pageNumber.push(i)
  }
  

  // get Current post 
  const  indexOfLastPost = currentPage * postPerPage;
  const indexOfFirstPost = indexOfLastPost - postPerPage;
  const currentPosts = user.slice(indexOfFirstPost, indexOfLastPost);

  // changePage
   const paginate = pageNumber => setCurrentPage(pageNumber)

   // function 
   const handlePrev = () => {
       pageNumber = pageNumber- 1;
       
   }
   const handleNext = () => {
    pageNumber = pageNumber + 1;
    return pageNumber
}


  return (
    <>
      <div className="news-div">
        {
        currentPosts.map((curEle) => { 
        return (

                <div className="news-c-div" key={curEle.id}>
                <div className="img-div"><img src="\images\close.svg" alt="" /></div> 
                 <h1>{curEle.title}</h1>
                 <p>{curEle.body}</p>
                 </div>
        )
                 
        })
    }
        <nav>
            <ul className="pagination">
          <button onClick={handlePrev()}> <img src="\images\arrow_back.svg" alt="" /></button>
           {pageNumber.map(number => (
               <li key={number} className="page-item">
                   <a  href="!#" onClick={() => paginate(number)} className="page-link">
                       {number}
                   </a>
               </li>
           ))}
          
          <button onClick={handleNext()}><img src="\images\arrow_forward.svg" alt="" /></button>
          </ul>
        </nav>
      </div>
    </>
  );
};

export default News;
