import React from "react";
import "./style.css"

const Pagination = (props) => {
  
  

  return (
    <>
      <nav className="nav-m-div">
        <ul className="pagination nav-div">
          <li>
            <button
              onClick={props.handlePrev}
              disabled={props.currentPage === 1}
              className="nav-button"
            >
              <img src="\images\arrow_back.svg" alt="" />
            </button>
          </li>
          {props.pageNumber.map((number) => (
            <li key={number} className="page-item ">
              <a
                href="!#"
                onClick={() => props.paginate(number)}
                className="page-link nav-li"
              >
                {number}
              </a>
            </li>
          ))}

          <li>
            <button
              onClick={props.handleNext}
              disabled={props.currentPage === 17}
              className="nav-button"
            >
              <img src="\images\arrow_forward.svg" alt="" />
            </button>
          </li>
        </ul>
      </nav>
    </>
  );
};

export default Pagination;
