import React, { useEffect, useState } from "react";
import "./style.css";
import Pagination from "./pagination";

const Newsverticle = () => {
  const [user, setUser] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [postPerPage] = useState(5);

  const getUser = async () => {
    const response = await fetch("https://jsonplaceholder.typicode.com/posts");
    setUser(await response.json());
  };

  useEffect(() => {
    getUser();
  }, []);

  //pagination
  const pageNumber = [];

  for (let i = 1; i <= 3; i++) {
    pageNumber.push(i);
  }

  // get Current post
  const indexOfLastPost = currentPage * postPerPage;
  const indexOfFirstPost = indexOfLastPost - postPerPage;
  const currentPosts = user.slice(indexOfFirstPost, indexOfLastPost);

  // changePage
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  // function
  const handlePrev = () => {
    const page = currentPage - 1;
    setCurrentPage(page);
  };
  const handleNext = () => {
    const page = currentPage + 1;
    setCurrentPage(page);
  };
  const handleDelete = (index) => {
    const newItem = user.filter((curEle) => {
      return curEle.id != index;
    });
    setUser(newItem);
  };
  return (
    <>
      <div>
        <div className="news-div-2">
          {currentPosts.map((curEle) => {
            return (
              <div>
                <div className="img-div-2">
                  <img
                    src="\images\close.svg"
                    alt=""
                    onClick={() => handleDelete(curEle.id)}
                  />
                </div>
                <div className="news-c-div-2" key={curEle.id}>
                  <h1>{curEle.title}</h1>
                  <p>{curEle.body}</p>
                </div>
                
              </div>
            );
          })}
        </div>

        <Pagination
          currentPage={currentPage}
          handlePrev={handlePrev}
          handleNext={handleNext}
          pageNumber={pageNumber}
          paginate={paginate}
        />
      </div>
    </>
  );
};

export default Newsverticle;
