import React, { useEffect, useState } from "react";
import "./style.css";

const News = () => {
  const [user, setUser] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [postPerPage]= useState(6)


  const getUser = async () => {
    const response = await fetch("https://jsonplaceholder.typicode.com/posts");
    setUser(await response.json());
  };

  useEffect(() => {
    getUser();
  }, []);
  
  //pagination 
  const pageNumber = [];
   
  for (let i=1; i<=3; i++){
      pageNumber.push(i)
  }
  

  // get Current post 
  const  indexOfLastPost = currentPage * postPerPage;
  const indexOfFirstPost = indexOfLastPost - postPerPage;
  const currentPosts = user.slice(indexOfFirstPost, indexOfLastPost);

  // changePage
   const paginate = pageNumber => setCurrentPage(pageNumber)

   // function 
   const handlePrev = () => { 

    const page = currentPage-1;
    setCurrentPage(page);
      
      
   }
   const handleNext = () => {
        const page = currentPage+1;
        setCurrentPage(page)
    
       }
    


  return (
    <>
      <div className="news-m-div">
          <div className="news-div">
        {
        currentPosts.map((curEle) => { 
        return (

                <div className="news-c-div" key={curEle.id}>
                <div className="img-div"><img src="\images\close.svg" alt="" /></div> 
                 <h1>{curEle.title}</h1>
                 <p>{curEle.body}</p>
                 </div>
        )
                 
        })
    }
    </div>
    <div className="nav-m-div">
        <nav >
            <ul className="pagination nav-div">
          <li><button onClick={handlePrev} disabled={currentPage===1} className="nav-button"> <img src="\images\arrow_back.svg" alt="" /></button></li>
           {pageNumber.map(number => (
               <li key={number} className="page-item ">
                   <a  href="!#" onClick={() => paginate(number)} className="page-link nav-li">
                       {number}
                   </a>
               </li>
           ))}
          
        <li></li>  <button onClick={handleNext} disabled={currentPage===17} className="nav-button"><img src="\images\arrow_forward.svg" alt="" /></button>
          </ul>
        </nav>
        </div>
      </div>
    </>
  );
};

export default News;
